    # Loading of data
    def load_portfolio(self, nric):
        my_cursor.execute("SELECT * FROM oneHealth.patient WHERE patientId = %s", (nric,))
        rows = my_cursor.fetchall()
        my_cursor.execute("SELECT * FROM oneHealth.patient WHERE patientId = %s", (rows[0][5],))
        rows1 = my_cursor.fetchall()

        # Display
        self.lineEdit.setText(rows[0][1]) #fullname
        self.lineEdit_2.setText(rows[0][2])#contatc
        self.lineEdit_3.setText("Male" if rows[0][7] == 1 else "Female")#gender
        self.lineEdit_4.setText(rows[0][6])#bloodType
        self.lineEdit_5.setText(rows1[0][1])#NOK name
        self.lineEdit_6.setText(rows[0][0])#NRIC
        self.lineEdit_7.setText(rows[0][4].strftime("%m/%d/%Y, %H:%M:%S"))#DOB
        if rows[0][7] == 0:
            self.lineEdit_8.setText("Yes" if rows[0][8] == 1 else "No")  # pregnancy
        else:
            self.lineEdit_8.setText("N.A")

        self.lineEdit_9.setText(rows[0][9])#nationality
        self.lineEdit_10.setText(rows1[0][2])#NOK contact
        self.patient_chronicConditionInput.appendPlainText(rows[0][3])
